namespace Magezon\CustomerAttachments\Model\Api\SearchCriteria\CollectionProcessor\FilterProcessor;

use Magento\Framework\Api\Filter;
use Magento\Framework\Api\SearchCriteria\CollectionProcessor\FilterProcessor\CustomFilterInterface;
use Magento\Framework\Data\Collection\AbstractDb;

class AttachmentWebsiteFilter implements CustomFilterInterface
{
    /**
     * Apply custom website filter to collection
     *
     * @param Filter $filter
     * @param AbstractDb $collection
     * @return bool
     */
    public function apply(Filter $filter, AbstractDb $collection)
    {
        /** @var \Magezon\CustomerAttachments\Model\ResourceModel\Attachment\Collection $collection */
        $collection->addWebsiteFilter($filter->getValue(), false);

        return true;
    }
}
